import React, { useContext } from 'react'
import { useState, useEffect } from 'react';
import {checkWeather} from '../js/watherScript.js';
import { capitalizeFirstLetter } from '../js/variables.js';
import { AppManagerContext } from '../js/weatherAppManager.js';

export const SearhForm = (props)  => {
  const [cityNames, setCityNames] = useState(['Madrid', 'London']);
  const {inputText} = useContext(AppManagerContext);
  const checkData = async function(inputText){
    const str = capitalizeFirstLetter(inputText.toLowerCase());
    var data = await checkWeather(str);

    if (data.hasOwnProperty('main') && !cityNames.includes(str)){
        setCityNames([...cityNames, str]);
        console.log("str " + str);
        props.setSelectedCity(str);

    }else{
        props.setInputText('');
    }
}


  const handleSubmit = async function(e) {
    // Prevent the browser from reloading the page
    e.preventDefault();
    checkData(props.inputText);
  } 
  const onChangeHandle = function (e) {
    props.setInputText(e.target.value);

  };

  return (
    <form className="d-flex searchForm" onSubmit={handleSubmit} >
        <input className={"form-control " + props.state}  type="search" placeholder="Write a city..." aria-label="Search" 
        value={props.inputText} onChange={onChangeHandle}/> 
    </form>
  )
}
