import React from 'react'
import { useState, useEffect } from 'react';
import {checkWeather} from '../js/watherScript'
import { capitalizeFirstLetter } from '../js/variables';

import { CityCard } from './CityCard'

export const CitiesList = (props) => {
  const [currentId, setCurrentId] = useState(2);

  const [cityList, setCityList] = useState ([{city: 'Madrid', temp: '38', id:0}, 
  {city: 'London', temp: '11', id:1}]);
  const [cityNames, setCityNames] = useState(['madrid', 'london']);
  const items = cityList.map((item) =>
        <li key={item.id} onClick={onClick}><CityCard data = {[item.city, item.temp]}/></li>
  );

  useEffect(() => {
    //Runs on the first render
    //And any time any dependency value changes
    if (props.newItem){
      addCity();
      props.setNewItem(false);
    }
  }, [props.newItem]);

  const onClick = function () {  
    props.setClickItem(true);
  }
  const addCity =  async function () {
    getWeather(props.inputText);
  }

  const getWeather = async function(c) {
    // Prevent the browser from reloading the page
    let data = await checkWeather(c);
    if (data.hasOwnProperty('main') && !cityNames.includes(c.toLowerCase())){
      setCityList([...cityList, {city: capitalizeFirstLetter(props.inputText.toLowerCase()), temp: data.main.temp.toFixed(0), id: currentId}]);
      setCityNames([...cityNames, c.toLowerCase()]);
      setCurrentId(currentId + 1);
    }

  } 

  return (
    <div className='row citiesList'>
        <div className='col-12'>
            <ul>
               {items}
            </ul>
        </div>
    </div>
  )
}
