import React from 'react'
import $ from 'jquery';
import plusIcon from '../img/buttons/plusIcon.png';

export const SearchComponent = () => {
    var isRotated = false;
    const handleClick = () =>{
        if(isRotated){
            $('.AddRemoveButton').css({
                transform: 'rotate(0deg)',
                transition: '0.5s'
            });
            $('.form-control').css({
                width: '0px',
                transition: '0.5s',
                padding: '0px'
            });
        }else{
            $('.AddRemoveButton').css({
                transform: 'rotate(45deg)',
                transition: '0.5s'
            });
            $('.form-control').css({
                width: '200px',
                transition: '0.5s'
            });
        }

        isRotated = !isRotated;
    }
  return (
    <div>
       <form className="d-flex searchForm">
            <input className="form-control" type="search" placeholder="Search" aria-label="Search"/>
        </form>
        <button className='AddRemoveButton btn' onClick={handleClick}>
            <div className='boxImg'>
                <img src={plusIcon} alt="Plus Icon"/>
            </div>
        </button>
    </div>
  )
}
