import React, { useEffect, useState } from 'react';
import cloud_1 from '../img/backgrounds/cloud_1.svg';
import cloud_2 from '../img/backgrounds/cloud_2.svg';



export const Background = (props) => {
  const [state, setState] = useState('Down');
  const [windowSize, setWindowSize] = useState([
    window.innerWidth,
    window.innerHeight,
  ]);

  useEffect(() => {
    const handleWindowResize = () => {
      setWindowSize([window.innerWidth, window.innerHeight]);
    };

    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    };
  }, [windowSize]);

  useEffect (() => {
    (props.data.weather.name != '') ? setState('Up') : setState('Down');
  }, [props.data]);
  useEffect(() =>{

  },[props.data]);
  return (
    <div className={'background'+ state}>
        <div className='white_background'>
          if({windowSize[0] > 90}){
              <img className='cloud_1' src={cloud_1} alt='cloud'/>
          }else{
            <img className='cloud_1' src={cloud_2} alt='cloud'/>

          }

        </div>
    </div>

  )
}
