import React, { useState } from 'react'
import { DM } from './DM';
import { DMForm } from './DMForm';
import { DMHeadline } from './DMHeadline';

export const DMWindow = () => {
    const [messageList, setMessafeList] = useState([{text: 'pepa ol olo olo lo o o o o o oo o oo o o o o oo o oo oo o o o oo', hour: '10:00 p.m', messageState: 'Seen', author: 'Paco', id: 0},
    {text: 'pepo', hour: '11:00 p.m', messageState: 'Send', author: 'Manolo', id: 1},
    {text: 'pepa', hour: '10:00 p.m', messageState: 'Seen', author: 'Paco', id: 2}
  ]);
    const messages = messageList.map((message) =>
    <li key={message.id}><DM text= {message.text} hour = {message.hour} 
    messageState = {message.messageState} author = {message.author}/></li>
);
  return (
    <div className='DMWindow'>
        <DMHeadline/>
        <div className='DMList'>
            <ul>{messages}</ul>
        </div>
        <DMForm/>
    </div>
  )
}
