import './css/style.css';
import React, { useState, useEffect, useRef} from 'react';

import { windowSize, resize } from './js/weatherAppManager';
import { checkForecast, checkWeather } from './js/watherScript';


import { CitiesSection } from './components/CitiesSection';
import { DataSection } from './components/DataSection';
import { Background } from './components/Background';


function App() {
  const [inputText, setInputText] = useState ('');
  const [selectedCity, setSelectedCity] = useState('');
  const [dataHidden, setDataHidden] = useState (true);

  const [data, setData] = useState({
    weather: {
      name: '', 
      main: {
        humidity: 0,
        pressure: 0, 
        temp: 0.00,
        temp_max: 0.00,
        temp_min: 0.00 
      },
      visibility: 0,
      wind: {speed: 0}
    },
    forecast : [{
      dt : 1688245200,
      main :{
        temp: 0
      }
    }]
  });
  const [windowSize, setWindowSize] = useState([
    window.innerWidth,
    window.innerHeight,
  ]);

  useEffect(() => {
    const handleWindowResize = () => {
      setWindowSize([window.innerWidth, window.innerHeight]);
    };

    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    };
  }, [windowSize]);

  useEffect (() => {
    if(selectedCity != ''){
     getData(selectedCity); 
     setInputText('');
    }
  }, [selectedCity]);

  useEffect (() => {
    console.log(data);
  }, [data]);


  const getData= async function(city){
    const dat = await checkWeather(city);
    const forecast = await checkForecast(city)
    setData({
      ...data, // copy the current properties of "json"
      weather: {
        name: dat.name,
        main: {
          humidity: dat.main.humidity,
          pressure: dat.main.pressure, 
          temp: dat.main.temp,
          temp_max: dat.main.temp_max,
          temp_min: dat.main.temp_min 
        },
        visibility: dat.visibility,
        wind: {
          speed: dat.wind.speed
        }
      },
      forecast : forecast.list
    });
  }

  let submitInput = function (){
    console.log(inputText);
  }

  return (
    <div className="App">

    <CitiesSection inputText= {inputText} setInputText = {setInputText} submitInput= {submitInput}
    selectedCity = {selectedCity} setSelectedCity = {setSelectedCity} data = {data} 
    dataHidden = {dataHidden} setDataHidden = {setDataHidden} windowSize = {windowSize}/>

    {windowSize[0] > 900 ? <DataSection inputText= {inputText}  data = {data} selectedCity = {selectedCity} 
    dataHidden = {dataHidden} setDataHidden = {setDataHidden} setSelectedCity = {setSelectedCity} 
    setData={setData} windowSize = {windowSize}/> : 
     <DataSection inputText= {inputText}  data = {data} selectedCity = {selectedCity}
     setSelectedCity = {setSelectedCity} setData={setData} windowSize = {windowSize}/>}

     <Background data = {data} selectedCity = {selectedCity} windowSize = {windowSize}/>
    </div>
  );
}

export default App;
