import React from 'react'
import { useState } from 'react';
import $ from 'jquery';

import sunIcon from '../img/forecast/sunIcon_forecast.svg';
import {weatherData} from '../js/watherScript.js';
import {metricIcons, metrics, units} from '../js/variables.js'

export const MetricItem = ({type}) => {
  var setMetricsData;
  const [icon, setIcon] = useState(metricIcons[0]);
  const [metric, setMetric] = useState('Wind Speed');
  const [value, setValue] = useState('10');
  const [unit, setUnit] = useState(units[0]);



  return (
    <div className='MetricItem'>
        <div className='boxImg'>
            <img src={sunIcon} alt='Metric Icon'/>
        </div>
        <div className='boxInfo'>
            <p className='value'>{value}<span>{unit}</span></p>
            <p className='metric'>{metric}</p>
        </div>
    </div>
  )
}
