import { useState, useContext, useEffect } from 'react';
import React from 'react'

import * as social from '../../js/MediaApp/mediaScript'

export const MediaAppManagerContext = React.createContext();

export function MediaAppManagerContextProvider (props) {

    /* VARIABLES */
    //Global
    const [windowSize, setWindowSize] = useState([window.innerWidth, window.innerHeight,]);
    const [inputText, setInputText] = useState ('');
    const [messageType, setMessageType] = useState('Text');
    const [user, setUser] = useState ('Manolo');

    const [messageID, setMessageID] = useState (3);
    const [messageList, setMessafeList] = useState([{text: 'pepa ol olo olo lo o o o o o oo o oo o o o o oo o oo oo o o o oo', hour: '10:00 p.m', messageState: 'Seen', author: 'Paco', id: 0},
    {text: 'pepo', hour: '11:00 p.m', messageState: 'Send', author: 'Manolo', id: 1},
    {text: 'pepa', hour: '10:00 p.m', messageState: 'Seen', author: 'Paco', id: 2}
    ]);

    useEffect (() => {
        const interval = setInterval(() => {
            a(1)
        }, 1000);
    }, []);

    const a = async function (id){
        const messageListJSON= await social.getMessageListByChatId(id);
        const users = await social.getUserUserNameListChatId(id);


        for (var i; i < messageList.length ; i++){
            setMessafeList(...messageList, [{text: messageListJSON[i].text, hour: messageListJSON[i].time, 
                messageState: messageListJSON[i].state, author: users[i], id: i}]);

        }

    }

    /* FUNCTIONS */
    const handleSubmit =  function(e) {
        // Prevent the browser from reloading the page
        e.preventDefault();
        addNewMessaage ();
    } 

    const onChangeHandle = function (e) {
        setInputText(e.target.value);

    };

    const addNewMessaage = function (){
        setMessafeList ([...messageList, 
            {text: inputText, hour: '10:00 p.m', messageState: 'Send', author: user, id: messageID} ]);
        setMessageID(messageID +1);
        setInputText('');

    }

    return (
        <>
            <MediaAppManagerContext.Provider value={{windowSize, inputText, setInputText, messageType, handleSubmit, onChangeHandle, user, messageList}}>
                {props.children}
            </MediaAppManagerContext.Provider>
        </>
    );
}
