var apiURL = 'http://localhost:8080';

export async function getUsers (){
    const response = await fetch(apiURL+'/users', {
        method: 'GET'
    }).then(response => response.json());
    return response;
}

export async function getUserById (id){
    const response = await fetch(apiURL+'/users/'+id, {
        method: 'GET'
    }).then(response => response.json());
    return response;
}

export async function getUserByNick (nick){
    const response = await fetch(apiURL+'/users/nick/'+nick,  {
        method: 'GET'
    }).then(response => response.json());
    return response;
}

export async function getUserByUserName (userName){
    const response = await fetch(apiURL+'/user/nick/'+userName,  {
        method: 'GET'
    }).then(response => response.json());
    return response;
}

export async function getChatsByUserId (id){
    const response = await fetch(apiURL+'/user/'+id+'/chats', {
        method: 'GET'
    }).then(response => response.json());
    return response;
}

export async function postUser (user){
    const response = await fetch(apiURL+'/users', {
        method: 'POST',
        body: JSON.stringify(
            {
                "userName": "Manolo",
                "nick": "MeLaSudaSoyManolo",
                "email": "manolo@gmail.com"
            }
        )
    }).then(response => response.json());
    return response;
}





