import {days} from '../js/variables.js';

var apiKey = 'f9892889e00ba696ec9d7c610b9e3f38';
var apiURL = 'https://api.openweathermap.org/data/2.5/weather?&units=metric&q=yeles';
var apiURL_forecast = 'https://api.openweathermap.org/data/2.5/forecast?&units=metric&q=yeles';

async function checkWather (){
    const response = await fetch(apiURL + '&appid='+apiKey);
    var data = await response.json();
    return data;
}
async function checkForecast (){
    const response = await fetch(apiURL_forecast + '&appid='+apiKey);
    var data = await response.json();
    return data;
}
export let weatherData =  await checkWather();
export let forecastData =  await checkForecast();

export const getWind =  function(){
    return (weatherData.wind.speed * 3.6).toFixed(0); 
}

export const getVisibility =  function(){
    return (weatherData.visibility /1000).toFixed(0); 
}

export const getMaxTempt =  function(day){
    let values = [];
    let i = 0;
    let dayTxt = new Date(forecastData.list[i].dt* 1000).getDay() -1;
    console.log('DATE + DAY '+ dayTxt + " " + day );

    while(dayTxt <= day){
        if(dayTxt == day){
            values.push(forecastData.list[i].main.temp);
        }
        i++;
        dayTxt = new Date(forecastData.list[i].dt* 1000).getDay() -1;
        console.log('DATE + DAY '+ dayTxt + " " + day + " " + i);

    }
    return Math.max(...values);
}

console.log('MAAX VVALUE '+ getMaxTempt(2));
console.log(forecastData);

export const getMinTempt =  function(day){
    let values = [];
    for (var i= day*5 ; i< day*5 +5; i++){
        values.push(forecastData.list[i].main.temp);
    }
    return Math.min(...values);
}

export const getDay = function(i){
    let dayTxt = new Date(forecastData.list[i].dt* 1000).getDay() -1;
    return days[dayTxt];
}
