import React, { useEffect, useState } from 'react';
import cloud_1 from '../img/backgrounds/cloud_1.svg';
import cloud_2 from '../img/backgrounds/cloud_2.png';


export const Background = (props) => {
  const [state, setState] = useState('down');
  useEffect(() =>{
    if(props.data.weather.main != ''){
      setState('up');
    }
  },[props.data]);
  return (
    <div className={'background'+ state}>
        <div className='white_background'>
          <img className='cloud_1' src={cloud_1} alt='cloud'/>
          <img className='cloud_2' src={cloud_2} alt='cloud'/>
          <img className='cloud_3' src={cloud_2} alt='cloud'/>


        </div>
    </div>

  )
}
