import React, { useRef } from 'react'
import $ from 'jquery';
import { useState, useEffect } from 'react';

import { SearhForm } from './SearhForm.js'
import plusIcon from '../img/buttons/plusIcon.png';


export const SearchComponent = () => {
    var [isRotated, setIsRotated] = useState(false);
    let buttonRef = useRef();

    useEffect(() => {
        let handler = (e) =>{
            if(buttonRef.current.contains(e.target) && isRotated){

            }
        }
        document.addEventListener('mousedown', handler);
    });


    const handleClick = () =>{
        if(isRotated){
            $('.AddRemoveButton').css({
                transform: 'rotate(0deg)',
                transition: '0.5s'
            });
        }else{
            $('.AddRemoveButton').css({
                transform: 'rotate(45deg)',
                transition: '0.5s'
            });
        }

        setIsRotated(!isRotated);
    }
  return (
    <div className='row'>
         <div className='col-12 SearchComponent'>
         {isRotated ? <SearhForm state={'hidden'}/> :<SearhForm state={'ative'}/>}
            <button className='AddRemoveButton btn' onClick={handleClick} ref={buttonRef}>
                <div className='boxImg'>
                    <img src={plusIcon} alt="Plus Icon"/>
                </div>
            </button>

        </div>
    </div>
  )
}
