import { useState, useContext } from 'react';
import React from 'react'

export const Manager = React.createContext(AppManager);

export function useAppManager (type){
    return useContext(AppManager(type));
}

export function AppManager ({children}) {
    const [windowSize, setWindowSize] = useState([window.innerWidth, window.innerHeight,]);
    const [inputText, setInputText] = useState ('');

    const manager = {
        window: [windowSize, setWindowSize]
    };
    return (
        <Manager.Provider value={manager} type ={children.type} >
            {children}
        </Manager.Provider>
    );
}
