import React, { useEffect } from 'react'
import { useState } from 'react';

import { CitiesList } from './CitiesList';
import { SearchComponent } from './SearchComponent';

export const CitiesSection = (props) => {
  const [state, setState] = useState('Active');
  const [windowSize, setWindowSize] = useState([
    window.innerWidth,
    window.innerHeight,
  ]);

  useEffect(() => {
    const handleWindowResize = () => {
      setWindowSize([window.innerWidth, window.innerHeight]);
    };

    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    };
  }, [windowSize]);

  useEffect (() => {
    (props.selectedCity == '') ?  setState('Active') :  setState('Hidden');

  }, [props.data]);

  return (
    <div className={'container-fluid citySection' + state}>
        <SearchComponent inputText = {props.inputText} setInputText = {props.setInputText}
        setSelectedCity = {props.setSelectedCity}/>
        <CitiesList inputText = {props.inputText} selectedCity = {props.selectedCity} setSelectedCity = {props.setSelectedCity}/>
    </div>
  )
}
