import { type } from '@testing-library/user-event/dist/type';
import { useState, useContext } from 'react';
import React from 'react'

export const Manager = React.createContext(AppManager);

export function useAppManager (type){
    return useContext(AppManager(type));
}

export function AppManager (props) {
    const [windowSize, setWindowSize] = useState([window.innerWidth, window.innerHeight,]);
    const [inputText, setInputText] = useState ('');

    const manager = {
        window: windowSize
    };
    return (
        <Manager.Provider value={props}>
            {props.children}
        </Manager.Provider>
    );
}
