import React, { useRef } from 'react'
import $ from 'jquery';
import { useState, useEffect } from 'react';

import { SearhForm } from './SearhForm.js'

import { PlusButton } from './PlusButton.js';


export const SearchComponent = (props) => {
    var [isRotated, setIsRotated] = useState(false);

    const handleClick = () =>{
        setIsRotated(!isRotated);
    }
    function onClick (num){
        setIsRotated(!isRotated);
        //console.log({inputTex});
    }

  return (
    <div className='row'>
         <div className='col-12 SearchComponent'>
            {isRotated ? <SearhForm state={'hidden'} inputText = {props.inputText} setInputText = {props.setInputText}/> : <SearhForm state={'ative'} inputText = {props.inputText} setInputText = {props.setInputText}/>}
            {isRotated ? <PlusButton state={'plusButton'} function= {onClick} /> : <PlusButton state={'XButton'} function = {onClick}/>}

        </div>
    </div>
  )
}
