import { type } from '@testing-library/user-event/dist/type';
import { useState, useContext, useEffect } from 'react';
import React from 'react'
import { checkWeather } from './watherScript';
import { capitalizeFirstLetter } from './variables';

export const AppManagerContext = React.createContext();

export function AppManagerContextProvider (props) {
    const [windowSize, setWindowSize] = useState([window.innerWidth, window.innerHeight,]);
    const [inputText, setInputText] = useState ('');
    const [selectedCity, setSelectedCity] = useState('');
    const [dataHidden, setDataHidden] = useState (true);

    //City List
    const [currentId, setCurrentId] = useState(2);
    const [cityList, setCityList] = useState ([{city: '', temp: 0, id:0}, 
    {city: '', temp: 0, id:1}]);
    const [cityNames, setCityNames] = useState(['Madrid', 'London']);

    const addCity = async function(c) {
        let data = await checkWeather(c);
        const str = capitalizeFirstLetter(c.toLowerCase());

        if (data.hasOwnProperty('weather') && !cityNames.includes(str)){
          setCityList([...cityList, {city: str, temp: data.main.temp.toFixed(0), id: currentId}]);
          setCurrentId(currentId + 1);
          setCityNames([...cityNames, str]);

        }
    } 

    useEffect(() => {
        const handleWindowResize = () => {
          setWindowSize([window.innerWidth, window.innerHeight]);
        };

        window.addEventListener('resize', handleWindowResize);

        return () => {
          window.removeEventListener('resize', handleWindowResize);
        };
      }, [windowSize]);

      useEffect (() => {
        if(selectedCity != ''){
         getData(selectedCity); 
         setInputText('');
        }
      }, [selectedCity]);

      useEffect (() => {
        console.log(data);
      }, [data]);


    return (
        <>
            <AppManagerContext.Provider value={{windowSize, inputText, setInputText, addCity, selectedCity, setSelectedCity}}>
                {props.children}
            </AppManagerContext.Provider>
        </>
    );
}
