import React from 'react'
import { useState } from 'react';
import $ from 'jquery';

import sunIcon from '../img/forecast/sunIcon_forecast.svg';
import {weatherData} from '../js/watherScript.js';
import {metricIcons, metrics, units} from '../js/variables.js'

export const MetricItem = ({type}) => {
  var setMetricsData;
  var getValue = function (type){
    switch (type){
      case 0:
        return weatherData.wind.speed;
      case 1:
        return weatherData.main.humidity;
      case 2:
        return weatherData.main.pressure;
      case 3:
        return weatherData.visibility;
    }
  }
  const [icon, setIcon] = useState(metricIcons[type]);
  const [metric, setMetric] = useState(metrics[type]);
  const [value, setValue] = useState(getValue(type));
  const [unit, setUnit] = useState(units[type]);


  return (
    <div className='MetricItem'>
        <div className='boxImg'>
            <img src={sunIcon} alt='Metric Icon'/>
        </div>
        <div className='boxInfo'>
            <p className='value'>{value}<span>{unit}</span></p>
            <p className='metric'>{metric}</p>
        </div>
    </div>
  )
}
