import React, { useEffect } from 'react'
import { useState } from 'react';

import { WeatherHeadline } from './WeatherHeadline';
import  {ForecastRow} from './ForecastRow'
import { ArrowButton } from './ArrowButton';
import { MetricItem } from './MetricItem'
import { weatherData, checkWeather } from '../js/watherScript';


export const DataSection = (props) => {
    const [max, setMax] = useState(weatherData.main.temp_max.toFixed(0));
    const [min, setMin] = useState(weatherData.main.temp_min.toFixed(0));
    const [state, setState] = useState('Hidden');
    const [data, setData] =  useState ();


    useEffect(() =>{
        (props.clickItem == true || props.newItem == true) ?  setState('Active') :  setState('Hidden');
    }, [props.newItem, props.clickItem]);
  return (
    <div className={'container-fluid dataSection' + state}>
        <ArrowButton setNewItem ={props.setNewItem} setClickItem = {props.setClickItem}/>
        <WeatherHeadline data={props.data}/>
        <div className='row'>
            <div className='col-12 maxMin'>
                <p className='max'>Max.: {max}ยบ</p>
                <p className='min'>Min.: {min}ยบ</p>
            </div>
        </div>
        <div className='row'>
            <div className='col-12 metricRow '>
                <MetricItem type= {0} />
                <MetricItem type= {1} />
            </div>
        </div>
          <ForecastRow/>
        <div className='row'>
            <div className='col-12 metricRow '>
                <MetricItem type= {2} />
                <MetricItem type= {3} />
            </div>
        </div>
    </div>
  )
}
