import './css/style.css';
import React, { useState, useEffect, useRef, useContext} from 'react';

import {AppManagerContextProvider} from './js/weatherAppManager';
import { checkForecast, checkWeather } from './js/watherScript';


import { CitiesSection } from './components/CitiesSection';
import { DataSection } from './components/DataSection';
import { Background } from './components/Background';


function App() {

  const [dataHidden, setDataHidden] = useState (true);

  const [data, setData] = useState({
    weather: {
      name: '', 
      main: {
        humidity: 0,
        pressure: 0, 
        temp: 0.00,
        temp_max: 0.00,
        temp_min: 0.00 
      },
      visibility: 0,
      wind: {speed: 0}
    },
    forecast : [{
      dt : 1688245200,
      main :{
        temp: 0
      }
    }]
  });
  const [windowSize, setWindowSize] = useState([
    window.innerWidth,
    window.innerHeight,
  ]);



  return (
    <AppManagerContextProvider>
    <div className="App">

    <CitiesSection data = {data} dataHidden = {dataHidden} setDataHidden = {setDataHidden} windowSize = {windowSize}/>

    {windowSize[0] > 900 ? <DataSection  data = {data}
    dataHidden = {dataHidden} setDataHidden = {setDataHidden} 
    setData={setData} windowSize = {windowSize}/> : 
     <DataSection   data = {data} setData={setData} windowSize = {windowSize}/>}

     <Background/>
    </div>

    </AppManagerContextProvider>

  );
}

export default App;
