import React from 'react'
import $ from 'jquery';
import plusIcon from '../img/buttons/plusIcon.png';

export const AddRemoveButton = () => {
    var isRotated = false;
    const handleClick = () =>{
        if(isRotated){
            $('.AddRemoveButton').css({
                transform: 'rotate(0deg)',
                transition: '0.5s'
            });
        }else{
            $('.AddRemoveButton').css({
                transform: 'rotate(45deg)',
                transition: '0.5s'
            });
        }

        isRotated = !isRotated;
    }
  return (
    <button className='AddRemoveButton btn' onClick={handleClick}>
        <div className='boxImg'>
            <img src={plusIcon} alt="Plus Icon"/>
        </div>
    </button>
  )
}
