import React, { useEffect } from 'react'
import { useState } from 'react';

import { CitiesList } from './CitiesList';
import { SearchComponent } from './SearchComponent';

export const CitiesSection = (props) => {
  const [state, setState] = useState('active');

  useEffect (() => {
    (props.clickItem == true || props.newItem == true) ?  setState('hidden') :  setState('active');
  }, [props.clickItem, props.newItem]);

  return (
    <div className={'container-fluid citySection' + {state}}>
        <SearchComponent inputText = {props.inputText} setInputText = {props.setInputText} submitInput = {props.submitInput}/>
        <CitiesList inputText = {props.inputText} newItem = {props.newItem} setNewItem = {props.setNewItem}
        clickItem = {props.clickItem} setClickItem = {props.setClickItem}/>
    </div>
  )
}
