import React from 'react'
import $ from 'jquery';
import { useState } from 'react';
import plusIcon from '../img/buttons/plusIcon.png';

export const SearchComponent = () => {
    var isRotated = false;
    const [inputText, setInputText] = useState('');
    const handleSubmit = function(e) {
        // Prevent the browser from reloading the page
        e.preventDefault();

        console.log(inputText);
    }
    const onChangeHandle = function (e) {
        setInputText(e.target.value);
    };
    const handleClick = () =>{
        if(isRotated){
            $('.AddRemoveButton').css({
                transform: 'rotate(0deg)',
                transition: '0.5s'
            });
            $('.form-control').css({
                width: '0%',
                transition: '0.5s',
                padding: '10px 0px'
            });
        }else{
            $('.AddRemoveButton').css({
                transform: 'rotate(45deg)',
                transition: '0.5s'
            });
            $('.form-control').css({
                width: '100%',
                transition: '0.5s',
                padding: '10px 20px'
            });
        }

        isRotated = !isRotated;
    }
  return (
    <div className='row'>
         <div className='col-12 SearchComponent'>
            <form className="d-flex searchForm" onSubmit={handleSubmit} >
                <input className="form-control" type="search" placeholder="Write a city..." aria-label="Search" 
                value={inputText} onChange={onChangeHandle}/>
            </form>
            <button className='AddRemoveButton btn' onClick={handleClick}>
                <div className='boxImg'>
                    <img src={plusIcon} alt="Plus Icon"/>
                </div>
            </button>
        </div>
    </div>
  )
}
