import React, { useRef } from 'react'
import $ from 'jquery';
import { useState, useEffect } from 'react';

import { SearhForm } from './SearhForm.js'
import plusIcon from '../img/buttons/plusIcon.png';


export const SearchComponent = () => {
    var isRotated = false;

    let buttonRef = useRef();

    useEffect(() => {
        let handler = (e) =>{
            if(buttonRef.current.contains(e.target) && isRotated){
                isRotated = true;
                $('.AddRemoveButton').css({
                    transform: 'rotate(0deg)',
                    transition: '0.5s'
                });
                $('.form-control').css({
                    width: '0%',
                    transition: '0.5s',
                    padding: '10px 0px'
                });

            }
        }
        document.addEventListener('mousedown', handler);
    });


    const handleClick = () =>{
        if(isRotated){
            $('.AddRemoveButton').css({
                transform: 'rotate(0deg)',
                transition: '0.5s'
            });
            $('.form-control').css({
                width: '0%',
                transition: '0.5s',
                padding: '10px 0px'
            });
            //setInputText('');
        }else{
            $('.AddRemoveButton').css({
                transform: 'rotate(45deg)',
                transition: '0.5s'
            });
            $('.form-control').css({
                width: '100%',
                transition: '0.5s',
                padding: '10px 20px'
            });
        }

        isRotated = !isRotated;
    }
  return (
    <div className='row'>
         <div className='col-12 SearchComponent'>
            <SearhForm state={'i'}/>
            <button className='AddRemoveButton btn' onClick={handleClick} ref={buttonRef}>
                <div className='boxImg'>
                    <img src={plusIcon} alt="Plus Icon"/>
                </div>
            </button>
        </div>
    </div>
  )
}
