import React, { useEffect } from 'react'
import { useState } from 'react';

import { WeatherHeadline } from './WeatherHeadline';
import  {ForecastRow} from './ForecastRow'
import { ArrowButton } from './ArrowButton';
import { MetricItem } from './MetricItem'


export const DataSection = (props) => {
    const [max, setMax] = useState(props.data.weather.main.temp_max.toFixed(0));
    const [min, setMin] = useState(props.data.weather.main.temp_min.toFixed(0));
    const [state, setState] = useState('Hidden');


    useEffect(() =>{
        (props.selectedCity != '') ?  setState('Active') :  setState('Hidden');
    }, [props.data]);

    const returnDataSectionMobile = function(){
        return (
            <>
                <div className='row'>
                <div className='col-12 metricRow '>
                        <MetricItem type= {0} value = {props.data.weather.wind.speed}/>
                        <MetricItem type= {1} value = {props.data.weather.main.humidity}/>
                    </div>
                </div>
                <ForecastRow forecast = {props.data.forecast}/>
                <div className='row'>
                    <div className='col-12 metricRow '>
                        <MetricItem type= {2}  value = {props.data.weather.main.pressure}/>
                        <MetricItem type= {3}  value = {props.data.weather.visibility/1000}/>
                    </div>
                </div>
            </>
        )
    }
    const returnDataSectionDesktop = function(){
        return (
            <>
                <div className='row'>
                    <ForecastRow forecast = {props.data.forecast}/>
                    <div className='col-12 metricRow '>
                        <MetricItem type= {0} value = {props.data.weather.wind.speed}/>
                        <MetricItem type= {1} value = {props.data.weather.main.humidity}/>
                        <MetricItem type= {2}  value = {props.data.weather.main.pressure}/>
                        <MetricItem type= {3}  value = {props.data.weather.visibility/1000}/>
                    </div>
                </div>
            </>
        )
    }
  return (
    <div className={'container-fluid dataSection' + state}>
        <ArrowButton setSelectedCity = {props.setSelectedCity} setData = {props.setData}/>
        <WeatherHeadline data={props.data} dataHidden = {props.dataHidden}/>
        {props.windowSize[0] < 900 ? returnDataSectionMobile() : returnDataSectionDesktop()}
    </div>
  )
}
