import './css/style.css';

import { useEffect, useState } from 'react';
import { CitiesSection } from './components/CitiesSection';
import { DataSection } from './components/DataSection';
import { checkWeather } from './js/watherScript';

function App() {
  const [inputText, setInputText] = useState ('');
  const [selectedCity, setSelectedCity] = useState('');
  const [data, setData] = useState({name:'', main: {humidity: 0,pressure: 0, temp: 0.00,
    temp_max: 0.00 ,temp_min: 0.00 }, wind: {speed: 0}});

  useEffect (() => {
    if(selectedCity != ''){
      getData(selectedCity);

    }

  }, [selectedCity]);

  const getData= async function(city){
    const dat = await checkWeather(city);
    setData({name:dat.name, main: {humidity: dat.main.humidity,pressure: 0, temp: 0.00,
      temp_max: 0.00 ,temp_min: 0.00 }, wind: {speed: 0}});
    console.log(dat.name);
    console.log(data);
  }

  let submitInput = function (){
    console.log(inputText);
  }

  return (
    <div className="App">
    <CitiesSection inputText= {inputText} setInputText = {setInputText} submitInput= {submitInput}
    selectedCity = {selectedCity} setSelectedCity = {setSelectedCity}/>
    <DataSection inputText= {inputText}  data = {data} 
    selectedCity = {selectedCity} setSelectedCity = {setSelectedCity} setData={setData}/>

    </div>
  );
}

export default App;
