import React from 'react'
import { useState } from 'react';
import {weatherData, getWind, getVisibility} from '../js/watherScript.js';
import {metricIcons, metrics, units} from '../js/variables.js'

export const MetricItem = ({props}) => {
  var setMetricsData;
  const getValue = function (v){
    switch (v){
      case 0:
        return getWind();
      case 1:
        return weatherData.main.humidity;
      case 2:
        return weatherData.main.pressure;
      case 3:
        return getVisibility();
    }
  }

  const [icon, setIcon] = useState(metricIcons[props.type]);
  const [metric, setMetric] = useState(metrics[props.type]);
  const [unit, setUnit] = useState(units[props.type]);


  return (
    <div className='MetricItem'>
        <div className='boxImg'>
            <img src={icon} alt='Metric Icon'/>
        </div>
        <div className='boxInfo'>
            <p className='value'>{props.value}<span>{unit}</span></p>
            <p className='metric'>{metric}</p>
        </div>
    </div>
  )
}
