import React, { useEffect } from 'react'
import { useState } from 'react';

import { CitiesList } from './CitiesList';
import { SearchComponent } from './SearchComponent';

export const CitiesSection = (props) => {
  const [state, setState] = useState('Active');

  useEffect (() => {
     (props.clickItem || props.newItem) ?  setState('Hidden') :  setState('Active');
  }, [props.clickItem, props.newItem]);

  return (
    <div className={'container-fluid citySection' + state}>
        <SearchComponent inputText = {props.inputText} setInputText = {props.setInputText} setNewItem =  {props.setNewItem}
        SelectedCity = {props.SelectedCity} setSelectedCity = {props.setSelectedCity}/>
        <CitiesList inputText = {props.inputText} newItem = {props.newItem} setNewItem = {props.setNewItem}
        clickItem = {props.clickItem} setClickItem = {props.setClickItem} SelectedCity = {props.SelectedCity} 
        setSelectedCity = {props.setSelectedCity}/>
    </div>
  )
}
