import { MediaAppManagerContext } from '../../../js/MediaApp/mediaAppManager';

export const DM = () => {
  const {messageType} = useContext(MediaAppManagerContext);
  const renderMenssage = function(){
    switch (messageType){
      case 'Text':
        return (<p className='message'>Lorem ipsum</p>);
      case 'Photo':
        return (<p className='message'> ipsum</p>);
    }

  }
  return (
    <div className='messageBox'>
      {renderMenssage()}
    </div>
  )
}
