import React, { useContext, useState } from 'react'
import { MediaAppManagerContext } from '../../../js/MediaApp/mediaAppManager';

export const DM = (props) => {
  const {messageType} = useContext(MediaAppManagerContext);
  const {user} =  useContext(MediaAppManagerContext);

  const [state, setState] = useState (props.author == user ?  'Self' : 'Foreign');

  const renderMenssage = function(){
    switch (messageType){
      case 'Text':
        return (<p className='text'>{props.text}</p>);
      case 'Photo':
        return (<p className='text'> ipsum</p>);
    }

  }
  return (
    <div className={state +'DM'}>
      <div className='DMBox'>
        {renderMenssage()}
      </div>
      <div className='info'>
        <p className='time'>{props.hour}</p>
        <span></span>
        {props.author == user ? <p className='state'>{props.messageState}</p> : null}
      </div>
    </div>
  )
}
