import React, { useContext } from 'react'
import { MediaAppManagerContext } from '../../../js/MediaApp/mediaAppManager';

export const DMForm = () => {
  const {inputText} = useContext(MediaAppManagerContext);
  const {handleSubmit} = useContext(MediaAppManagerContext);
  const {onChangeHandle} = useContext(MediaAppManagerContext);

  return (
    <div className='DMForm_box'>
        <form className="d-flex DMForm" onSubmit={handleSubmit} >
            <input className={"form-control "}  type="search" placeholder="Message..." aria-label="Search" 
            value={inputText} onChange={onChangeHandle}/> 
            {inputText != '' ? <button className='btn' onClick={handleSubmit}>Send</button>: null}
            {inputText == ''? <div>
            <button className='btn' onClick={handleSubmit}>Au</button>
            <button className='btn' onClick={handleSubmit}>Img</button>
            </div> : null}
        </form>
    </div>
  )
}
