import React, { useContext, useState } from 'react'
import { DM } from './DM';
import { DMForm } from './DMForm';
import { DMHeadline } from './DMHeadline';
import { MediaAppManagerContext } from '../../../js/MediaApp/mediaAppManager';


export const DMWindow = () => {
    const {messageList} = useContext(MediaAppManagerContext);
    const messages = messageList.map((message) =>
    <li key={message.id}><DM text= {message.text} time = {message.hour} 
    state = {message.messageState} author = {message.author}/></li>
);
  return (
    <div className='DMWindow'>
        <DMHeadline/>
        <div className='DMList'>
            <ul>{messages}</ul>
        </div>
        <DMForm/>
    </div>
  )
}
