import React, { useContext } from 'react'
import { useState, useEffect} from 'react';
import {checkWeather, getTemp} from '../js/watherScript'
import { capitalizeFirstLetter } from '../js/variables';
import { AppManager } from '../js/weatherAppManager';

import { CityCard } from './CityCard'

export const CitiesList = (props) => {
  const [currentId, setCurrentId] = useState(2);
  const onClick = function (city) {  
    props.setSelectedCity(city)
  }

  const [cityList, setCityList] = useState ([{city: '', temp: 0, id:0}, 
  {city: '', temp: 0, id:1}]);
  const [cityNames, setCityNames] = useState(['Madrid', 'London']);
  const items = cityList.map((item) =>
        <li key={item.id} onClick={() => onClick(item.city)}><CityCard data = {[item.city, item.temp]} delay = {item.id * 0.2 + 0.2}/></li>
  );

  /*
    This use effect check if theinputText's value has changed 
    to != ''. This useEffect add a new city to the cityList;

  */
  useEffect(() => {
    if (props.inputText != ''){
      addCityWrapper();
    }
  }, [props.selectedCity]);

  /*
    When cciityNames' value changes it refresh
   */
  useEffect(() => {
     refreshWeather();
  }, [cityNames]);

  /*
    This method wraps the addCity async method
  */
  const addCityWrapper =  async function () {
    addCity(props.inputText);
  }

  /*
    AddCity method get a string (a city name) and get all the data 
    related to that city. Then iitt checks if the ciity has already 
    been added to the cityList and cityNames, if not it will add it
  */
  const {addCity} =  useContext(AppManager)

  /*
    This meethod refresh all the city data
   */
  const refreshWeather = async function() {
   /*var data = [];
    for (let i = 0; i< cityNames.length; i++){
      data.push((await checkWeather(cityNames[i])));
    }*/


    setCityList([]);
    let arr = []
    for (let i = 0; i< cityNames.length; i++){
      var data = (await checkWeather(cityNames[i]));;
      arr.push({city: data.name, temp: data.main.temp.toFixed(0), id:i});
    }
    setCityList([...arr]);
  } 


  return (
    <div className='row citiesList'>
        <div className='col-12'>
            <ul>
               {items}
            </ul>
        </div>
    </div>
  )
}
