import React, { useContext, useState } from 'react'
import { MediaAppManagerContext } from '../../../js/MediaApp/mediaAppManager';

export const DMForm = () => {
  const {inputText} = useContext(MediaAppManagerContext);
  const {handleSubmit} = useContext(MediaAppManagerContext);
  const {onChangeHandle} = useContext(MediaAppManagerContext);
  //const {handleClickImageUpload} = useContext(MediaAppManagerContext);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleClickImageUpload =  function(e){
    setSelectedFile(e.target.files[0]);
  }

  const renderFormButtons = function(){
    return (
            <div className='formButtons'>
                <button className='uploadImageBtn btn'>
                  <label for="file-upload" class="custom-file-upload">
                      <i class="fa fa-cloud-upload"></i> Img
                  </label>
                  <input id="file-upload" type="file"/>
                </button>
                <button className='btn'>Img</button>
            </div> 
    );
  }

  return (
    <div className='DMForm_box'>
        <form className="d-flex DMForm" onSubmit={handleSubmit} >
            <input className={"form-control "}  type="search" placeholder="Message..." aria-label="Search" 
            value={inputText} onChange={onChangeHandle}/> 
            {inputText != '' ? <button className='btn' onClick={handleSubmit}>Send</button>: null}
            {inputText == ''? renderFormButtons(): null
            }
        </form>
    </div>
  )
}
