import React from 'react'
import { useState, useEffect } from 'react';
import {checkWeather, getTemp} from '../js/watherScript'
import { capitalizeFirstLetter } from '../js/variables';

import { CityCard } from './CityCard'

export const CitiesList = (props) => {
  const [currentId, setCurrentId] = useState(2);
  const onClick = function (city) {  
    props.setSelectedCity(city)
  }

  const [cityList, setCityList] = useState ([{city: 'Madrid', temp: 0, id:0}, 
  {city: 'London', temp: 0, id:1}]);
  const [cityNames, setCityNames] = useState(['Madrid', 'London']);
  const items = cityList.map((item) =>
        <li key={item.id} onClick={() => onClick(item.city)}><CityCard data = {[item.city, item.temp]}/></li>
  );


  useEffect(() => {
    //Runs on the first render
    //And any time any dependency value changes
    if (props.selectedCity != '' && props.inputText != ''){
      addCity();
    }
    updateWeather();

  }, [props.selectedCity]);


  const addCity =  async function () {
    getWeather(props.inputText);
  }

  const getWeather = async function(c) {
    // Prevent the browser from reloading the page
    let data = await checkWeather(c);
    const str = capitalizeFirstLetter(c.toLowerCase());

    if (data.hasOwnProperty('main') && !cityNames.includes(str)){
      setCityList([...cityList, {city: str, temp: data.main.temp.toFixed(0), id: currentId}]);
      setCityNames([...cityNames, str]);
      setCurrentId(currentId + 1);
    }

  } 
  const updateWeather = async function() {
    // Prevent the browser from reloading the page
    let data = [];
    for (let i = 0; i< cityNames.length; i++){
      data.push(await checkWeather(cityNames[i]));
    }
    console.log(data);

    setCityList([]);
    console.log(cityList);

    for (let i = 0; i< cityNames.length; i++){
      setCityList([...cityList],{city: data[i].name,  temp: data[i].main.temp.toFixed(0), id: i});
    }
    console.log('i');


  } 


  return (
    <div className='row citiesList'>
        <div className='col-12'>
            <ul>
               {items}
            </ul>
        </div>
    </div>
  )
}
