        <WeatherHeadline data={props.data}/>
        <div className='row'>
            <div className='col-12 maxMin'>
                <p className='max'>Max.: {props.data.weather.main.temp_max.toFixed(0)}ยบ</p>
                <p className='min'>Min.: {props.data.weather.main.temp_min.toFixed(0)}ยบ</p>
            </div>
        </div>
        <div className='row'>
            <div className='col-12 metricRow '>
                <MetricItem type= {0} value = {props.data.weather.wind.speed}/>
