import './css/style.css';
import React, { useState, useEffect } from 'react';
import { CitiesSection } from './components/CitiesSection';
import { DataSection } from './components/DataSection';
import { checkWeather } from './js/watherScript';

function App() {
  const [inputText, setInputText] = useState ('');
  const [selectedCity, setSelectedCity] = useState('');
  const [dato, setDato] = useState({
    name: '', 
    main: {
      humidity: 0,
      pressure: 0, 
      temp: 0.00,
      temp_max: 0.00,
      temp_min: 0.00 
    }, 
    wind: {speed: 0}
  });

  const [json, setJson] = useState({
    name: '',
    main: {
      humidity: 0,
      pressure: 0, 
      temp: 0.00,
      temp_max: 0.00,
      temp_min: 0.00 
    }, 
    wind: {
      speed: 0
    }
  });

  const updateName = () => {

    getData('o');
  };

  useEffect (() => {
    if(selectedCity != ''){
     getData(selectedCity);
    console.log(json);

    }

  }, [selectedCity]);

  const getData= async function(city){
    const dat = await checkWeather(city);

    setJson({
      ...json, // copy the current properties of "json"
      name: dat.name,
      main: {
        humidity: dat.main.humidity,
        pressure: dat.main.pressure, 
        temp: dat.main.temp,
        temp_max: dat.main.temp_max,
        temp_min: dat.main.temp_min 
      },
      wind: {
        speed: dat.wind.speed
      } // update the "name" property
    });
/*
    console.log(dat);
    setDato(({ ...dato, 
      name: 'siuuuu', 
      main: {
        humidity: dat.main.humidity,
        pressure: dat.main.pressure, 
        temp: dat.main.temp,
        temp_max: dat.main.temp_max,
        temp_min: dat.main.temp_min 
      }, 
      wind: {speed: dat.wind.speed}
    }));*/

  }

  let submitInput = function (){
    console.log(inputText);
  }

  return (
    <div className="App">
    <CitiesSection inputText= {inputText} setInputText = {setInputText} submitInput= {submitInput}
    selectedCity = {selectedCity} setSelectedCity = {setSelectedCity}/>
    <DataSection inputText= {inputText}  dato = {dato} 
    selectedCity = {selectedCity} setSelectedCity = {setSelectedCity} setDato={setDato}/>
    <button onClick={updateName}>Update Name</button>
    </div>
  );
}

export default App;
